INSERT INTO `book_detail` VALUES ('131', '调试九法', ' 9.0 ', '35.00', '9787115240576', '硬件缺陷和软件错误是“技术侦探”的劲敌，它们负隅顽抗，见缝插针。本书提出的九条简单实用的规则，适用于任何软件应用程序和硬件系统，可以帮助软硬件调试工程师检测任何bug，不管它们有多么狡猾和隐秘。', 'David J. Agans', '9cd53d8e467361849d45a9950166b4d624efed5c.jpg');
